import styles from "./UserForm.module.css";
import React, { useState } from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";

const UserForm = (props) => {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");

  const usernameChangeHandler = (event) => {
    if (event.target.value.trim().length > 0)
      setEnteredUsername(event.target.value.trim());
  };

  const ageChangeHandler = (event) => {
    if (event.target.value.trim().length > 0)
      setEnteredAge(event.target.value.trim());
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();

    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      setEnteredUsername("");
      setEnteredAge("");
      return;
    }

    if (+enteredAge.trim() < 1) {
      setEnteredUsername("");
      setEnteredAge("");
      return;
    }
    const userData = {
      username: enteredUsername,
      age: +enteredAge,
      id: Math.random(),
    };

    props.onSaveUserData(userData);

    setEnteredUsername("");
    setEnteredAge("");
  };

  return (
    <Card>
      <form onSubmit={onSubmitHandler}>
        <div className={styles["new-user__controls"]}>
          <div className={styles["new-user__control"]}>
            <label htmlFor="user">Username</label>
            <input
            id="username"
              type="text"
              value={enteredUsername}
              onChange={usernameChangeHandler}
            />
          </div>
          <div className={styles["new-user__control"]}>
            <label>Age (Years)</label>
            <input
              type="number"
              step="1"
              value={enteredAge}
              onChange={ageChangeHandler}
            />
          </div>
        </div>
        <div className={styles["new-user__actions"]}>
          <Button type="submit">Add User</Button>
        </div>
      </form>
    </Card>
  );
};

export default UserForm;
