import styles from "./UserForm.module.css";
import React, { useState } from "react";
import Button from "../UI/Button";

const UserForm = (props) => {

  const [isAgeValid,setIsAgeValid]=useState(true)

  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");

  const usernameChangeHandler = (event) => {
    if (event.target.value.trim().length > 0)
      setEnteredUsername(event.target.value.trim());
  };

  const ageChangeHandler = (event) => {
    if (event.target.value.trim().length > 0)
      setEnteredAge(event.target.value.trim());
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();

    if(Number(enteredAge)<0){
      setIsAgeValid(false)
      props.isAgeValid(isAgeValid)
    }
    else{

    const userData = {
      username: enteredUsername,
      age: +enteredAge,
      id: Math.random(),
    };

    props.onSaveUserData(userData);
    props.isAgeValid()
  }
    setEnteredUsername("");
    setEnteredAge("");
  };

  return (
    <form onSubmit={onSubmitHandler}>
      <div className={styles["new-user__controls"]}>
        <div className={styles["new-user__control"]}>
          <label>Username</label>
          <input
            type="text"
            value={enteredUsername}
            onChange={usernameChangeHandler}
          />
        </div>
        <div className={styles["new-user__control"]}>
          <label>Age (Years)</label>
          <input
            type="number"
            step="1"
            value={enteredAge}
            onChange={ageChangeHandler}
          />
        </div>
      </div>
      <div className={styles["new-user__actions"]}>
        <Button type="submit">Add User</Button>
      </div>
    </form>
  );
};

export default UserForm;
