import styles from '/.Input.modulo'

const Input = (props) => {
  return (
    <div
      className={`${props.inputStyle} ${
        props.state.isValid === false ? props.invalid : ""
      }`}
    >
      <label htmlFor={props.labelHtml}>{props.children}</label>
      <input
        type={props.type}
        id={props.id}
        value={props.value}
        onChange={onChangeHandler}
        onBlur={onBlurHandler}
      />
    </div>
  );
};

export default Input;
