import styles from "./Input.module.css";

const Input = (props) => {



  return (
    <div
      className={`${styles.control} ${
        props.isValid === false ? props.invalid : ""
      }`}
    >
      <label htmlFor={props.id}>{props.label}</label>
      <input
      ref={inputR}
        type={props.type}
        id={props.id}
        value={props.value}
        onChange={props.onChange}
        onBlur={props.onBlur}
      />
    </div>
  );
};

export default Input;
